document.addEventListener("DOMContentLoaded",()=>{const e=document.querySelectorAll(".lazy-bg");e.forEach(e=>{const t=e.getAttribute("data-bg");e.style.backgroundImage=`url(${t})`,e.classList.add("loaded")})}),document.addEventListener("DOMContentLoaded",()=>{const e=document.querySelector(".menu-toggle"),t=document.querySelector(".nav-links");e.addEventListener("click",()=>{t.classList.toggle("active");const s=e.querySelector("i");t.classList.contains("active")?(s.classList.remove("fa-bars"),s.classList.add("fa-times")):(s.classList.remove("fa-times"),s.classList.add("fa-bars"))}),document.addEventListener("click",s=>{if(!s.target.closest("nav")&&t.classList.contains("active")){t.classList.remove("active");const s=e.querySelector("i");s.classList.remove("fa-times"),s.classList.add("fa-bars")}})});